var WebSocketServer=require("ws").Server,io=new WebSocketServer({port:3030});io.broadcast=function(t){io.clients.forEach(function(n){n.send(t)})},io.on("connection",function(e){e.on("open",function(){console.log("open")}),e.on("message",function(t){var n=JSON.parse(t);switch(n.type){case"add message":n.type="add to ul",n.id="socket_id_"+e._ultron.id,io.broadcast(JSON.stringify(n)),n.me="me",e.send(JSON.stringify(n))}}),e.on("close",function(){var t="socket_id_"+e._ultron.id,n={type:"remover person",id:t};io.broadcast(JSON.stringify(n))})});