UE.registerUI("button",function(e,t){e.registerCommand(t,{execCommand:function(){alert("execCommand:"+t)}});var n=new UE.ui.Button({name:t,title:t,cssRules:"background-position: -500px 0;",onclick:function(){e.execCommand(t)}});return e.addListener("selectionchange",function(){var r=e.queryCommandState(t);r==-1?(n.setDisabled(!0),n.setChecked(!1)):(n.setDisabled(!1),n.setChecked(r))}),n});